    $$('input').css({
        '-webkit-box-shadow': 'none',
        '-moz-box-shadow': 'none',
        'box-shadow': 'none',
        'outline': 'none'
    })
    
    // 去除光标
    $$('input').attr('readonlyunselectable', 'on');
});

