// Aajx Get读取 银行和 开户省 数据
Ext.Ajax.request({
    url: __ctxPath + '/thirdreg.do',
    params: {
        isMobile: "1"
        
    },
    method: 'GET',
    async: true,
    success: function (response) {
        // 解析json串为对象
        var responseText = Ext.util.JSON.decode(response.responseText);
        
        // 银行列表
        var listbankcode = responseText.listbankcode;
        
        // 开户省列表
        var bankcode = responseText.bankcode;
        
        // 获取开户行dom元素
        var kaihuhang = $$('#kaihuhang1');
            kaihuhang.length = 1;
            
        // 获取开户省dom元素
        var kaihusheng = $$('#kaihusheng1');
            kaihusheng.length = 1;
        
        // 动态创建标签
        var option;
        
        // 循环遍历银行列表
        for (var i = 0, len = listbankcode.length; i < len; i++) {
                option = document.createElement("option");
                 option.value = listbankcode[i].id;
                 option.text = listbankcode[i].bankName;
                 kaihuhang.append(option);
        }
        // 循环遍历开户省列表
        for (var i = 0, len = bankcode.length; i < len; i++) {
            option = document.createElement("option");
                 option.value = bankcode[i].code;
                 option.text = bankcode[i].name;
                 kaihusheng.append(option);
        }
            
    }
});

// 选择省份,选择城市
changeCity1 = function(){
	// 获取省份id
	var bankId = document.getElementById("kaihusheng1").value;
	
	// 请求ajax,通过省份选择城市
	Ext.Ajax.request({
		url: __ctxPath +"/user/checkbankcodeBpCustMember.do",
		params : {
			mobile : "1",
			bankId : bankId
         },
	    success : function(response) {
  		   	var responsetext = Ext.util.JSON.decode(response.responseText);
  		   	var responseText = responsetext.data;
    		var kaihushi = document.getElementById("kaihushi1");
    		kaihushi.length = 1;
    		for(var i = 0; i < responseText.length; i++) {
    			var Option = document.createElement("option");
    		     Option.value = responseText[i].code;
    		     Option.text = responseText[i].name;
    		     kaihushi.options.add(Option);
    		}
    		
    		// 选中第一个城市
    		$$('#kaihushi1 option:eq(1)').attr('selected','selected');
		}});
}

