	// 进入账号中心,重新登录一遍
	Ext.Ajax.request({
				url : __ctxPath + '/tologin.do',
				params : {
					isMobile : "1",
					loginname : userNames,
					password : pwd,
					isWeixin : "1"
				},
				method : 'POST',
				success : function(response) {
					var obj = Ext.util.JSON.decode(response.responseText);
					if (obj.success == true) {
						// 全局变量保存数据,好及时更新数据
						curUserInfoo = obj.data;

						// 给全局变量更新数据
						curUserInfo = obj.data;

						// 判断是否开通存管用户账号
						isCheckCard = curUserInfoo.isCheckCard == '1'
								? true
								: false;

						// 将对象转换json字符串
						var jsonStr = JSON.stringify(curUserInfo);

						// 给localStorage存入属性名
						localStorage.setItem('curUserInfo', jsonStr);

					}
				}
			});

} else {
	// 否则就是第没登录状态,跳转登录页
	mobileNavi.push(Ext.create('hrmobile.public.myhome.login', {}));
}

