// 封装Ext.toast样式
 function animatedAlert (msg, timeout){
    // 获取时间
    var timeout = Ext.isEmpty(timeout) ? 2000 : timeout;
    
     // 动画提示框
	Ext.toast('' +
        '<div style="' +
		    'padding:5px 0;' + 
		    'border-radius:5px;' +
            'color: #d9534f;' +
		    'background:rgba(255,255,255,1);' +
	      '">' + 
            msg + '!' + 
        '</div>',
	    timeout
	);
    
    // 去除内边距
	$$('div.x-stretched.x-toast-text').css('padding',0);
	
	// 更改默认样式
	$$('.x-toast').css({
	    'border': 'none',
	    'padding': '15px 0',
	    'background': 'white'
	});
 }

