          var money11 = responseText.money11;

          // 累计到账收益(元)
          var money12 = responseText.money12;

          // 还款中借款笔数(笔)
          var count = responseText.count;

          // 招标中借款笔数(笔)
          var count1 = responseText.count1;

          // 待回款投资笔数(笔)
